import { useEffect, useState } from "react";
import axios from "axios";
import jsPDF from "jspdf";

function Reports() {
  const [employees, setEmployees] = useState([]);
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().slice(0, 10)
  );

  useEffect(() => {
    axios
      .get("http://localhost:5000/employees")
      .then((res) => setEmployees(res.data))
      .catch((err) => console.error(err));
  }, []);

  // ===================== DATA PROCESSING =====================
  const totalEmployees = employees.length;

  const departmentsMap = {};
  employees.forEach((e) => {
    if (e.department) {
      departmentsMap[e.department] =
        (departmentsMap[e.department] || 0) + 1;
    }
  });

  const totalDepartments = Object.keys(departmentsMap).length;

  let topDepartment = "";
  let topDepartmentCount = 0;

  Object.entries(departmentsMap).forEach(([dept, count]) => {
    if (count > topDepartmentCount) {
      topDepartment = dept;
      topDepartmentCount = count;
    }
  });

  const insightMessage =
    topDepartment && topDepartmentCount > 0
      ? `Most employees are currently assigned to the ${topDepartment} department.`
      : "Employee distribution data is currently unavailable.";

  // ===================== PDF EXPORT =====================
  const exportPDF = () => {
    const doc = new jsPDF();
    const generatedAt = new Date().toLocaleString();

    doc.setFontSize(16);
    doc.text("Employee Management System", 20, 20);

    doc.setFontSize(12);
    doc.text("Administrative Report", 20, 32);

    doc.setFontSize(10);
    doc.text(`Report Date: ${selectedDate}`, 20, 42);
    doc.text(`Generated At: ${generatedAt}`, 20, 48);

    doc.line(20, 52, 190, 52);

    let y = 65;

    doc.setFontSize(11);
    doc.text(`Total Employees: ${totalEmployees}`, 20, y);
    y += 10;

    doc.text(`Total Departments: ${totalDepartments}`, 20, y);
    y += 10;

    doc.text(`System Status: Active`, 20, y);
    y += 10;

    doc.text(
      `Top Department: ${topDepartment || "N/A"} (${topDepartmentCount})`,
      20,
      y
    );
    y += 15;

    doc.setFontSize(12);
    doc.text("Department Breakdown:", 20, y);
    y += 10;

    doc.setFontSize(10);
    Object.entries(departmentsMap).forEach(([dept, count]) => {
      doc.text(`â€¢ ${dept}: ${count} employees`, 25, y);
      y += 8;
    });

    doc.save("EMS_Report.pdf");
  };

  // ===================== UI =====================
  return (
    <div style={page}>
      <h1 style={title}>Reports</h1>
      <p style={subtitle}>Administrative & Operational Summary</p>

      {/* Filters */}
      <div style={filterBar}>
        <label>
          Select Date:{" "}
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </label>

        <button style={btn} onClick={exportPDF}>
          Export PDF
        </button>
      </div>

      {/* KPI GRID */}
      <div style={kpiGrid}>
        <div style={card}>
          <h3>Total Employees</h3>
          <p style={kpiNumber}>{totalEmployees}</p>
        </div>

        <div style={card}>
          <h3>Departments</h3>
          <p style={kpiNumber}>{totalDepartments}</p>
        </div>

        <div style={card}>
          <h3>System Status</h3>
          <p style={{ color: "#16a34a", fontWeight: "bold" }}>Active</p>
        </div>
      </div>

      {/* Insight */}
      <div style={insightBox}>
        ðŸ’¡ <strong>System Insight:</strong> {insightMessage}
      </div>
    </div>
  );
}

export default Reports;

/* ===================== STYLES ===================== */

const page = {
  padding: "32px",
  background: "#f8fafc",
  minHeight: "100vh",
};

const title = {
  fontSize: "28px",
  fontWeight: "bold",
};

const subtitle = {
  color: "#475569",
  marginBottom: "24px",
};

const filterBar = {
  display: "flex",
  gap: "16px",
  alignItems: "center",
  marginBottom: "30px",
};

const kpiGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
  gap: "20px",
};

const card = {
  background: "#ffffff",
  padding: "20px",
  borderRadius: "10px",
  boxShadow: "0 10px 20px rgba(0,0,0,0.08)",
};

const kpiNumber = {
  fontSize: "32px",
  fontWeight: "bold",
  marginTop: "10px",
};

const btn = {
  padding: "10px 16px",
  background: "#4f46e5",
  color: "#fff",
  border: "none",
  borderRadius: "6px",
  cursor: "pointer",
};

const insightBox = {
  marginTop: "30px",
  padding: "14px 18px",
  background: "#eef2ff",
  borderRadius: "8px",
  color: "#1e3a8a",
  fontSize: "14px",
};
