import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

import { Bar, Doughnut } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard() {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/employees")
      .then((res) => setEmployees(res.data))
      .catch((err) => console.error(err));
  }, []);

  const totalEmployees = employees.length;
const hour = new Date().getHours();
const greeting =
  hour < 12
    ? "Good morning"
    : hour < 18
    ? "Good afternoon"
    : "Good evening";

  const departmentsMap = {};
  employees.forEach((e) => {
    departmentsMap[e.department] =
      (departmentsMap[e.department] || 0) + 1;
  });

  const departmentChartData = {
    labels: Object.keys(departmentsMap),
    datasets: [
      {
        label: "Employees",
        data: Object.values(departmentsMap),
        backgroundColor: "#4f46e5",
      },
    ],
  };

  const attendanceChartData = {
    labels: ["Present", "Absent"],
    datasets: [
      {
        data: [totalEmployees * 0.75, totalEmployees * 0.25],
        backgroundColor: ["#22c55e", "#ef4444"],
      },
    ],
  };

  return (
    <div style={page}>
      <h1>Employee Management System</h1>
<p style={{ color: "#6b7280", marginTop: "4px" }}>
  Administrative Dashboard ¬∑ Final Year Project
</p>
<p style={{ marginTop: "12px", fontSize: "16px" }}>
  {greeting}, Administrator üëã
</p>
<p style={{ color: "#16a34a", fontSize: "14px", marginBottom: "20px" }}>
  ‚óè System running normally
</p>
      <p style={subtitle}>Employee Management Overview</p>

      {/* KPI Cards */}
      <div style={kpiGrid}>
        <div style={card}>
          <h3>Total Employees</h3>
          <p style={kpiNumber}>{totalEmployees}</p>
        </div>

        <div style={card}>
          <h3>Departments</h3>
          <p style={kpiNumber}>{Object.keys(departmentsMap).length}</p>
        </div>

        <div style={card}>
          <h3>System Status</h3>
          <p style={{ color: "#22c55e", fontWeight: "bold" }}>Active</p>
        </div>
      </div>

      {/* Charts */}
      <div style={chartGrid}>
        <div style={card}>
          <h3>Employees by Department</h3>
          <Bar data={departmentChartData} />
        </div>

        <div style={card}>
          <h3>Attendance Snapshot</h3>
          <Doughnut data={attendanceChartData} />
        </div>
      </div>

      {/* Quick Actions */}
      <div style={{ marginTop: "40px" }}>
        <h3>Quick Actions</h3>

        <Link to="/employees">
          <button style={btn}>Manage Employees</button>
        </Link>

        <Link to="/attendance">
          <button style={btn}>Attendance</button>
        </Link>

        <Link to="/profile">
          <button style={btn}>Admin Panel</button>
        </Link>
      </div>
    </div>
  );
}

/* ================== STYLES ================== */

const page = {
  padding: "30px",
  background: "#f8fafc",
  minHeight: "100vh",
};

const title = {
  fontSize: "28px",
  fontWeight: "bold",
};

const subtitle = {
  color: "#64748b",
  marginBottom: "30px",
};

const kpiGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
  gap: "20px",
};

const chartGrid = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fit, minmax(360px, 1fr))",
  gap: "24px",
  marginTop: "40px",
};

const card = {
  background: "#ffffff",
  padding: "20px",
  borderRadius: "10px",
  boxShadow: "0 10px 20px rgba(0,0,0,0.08)",
};

const kpiNumber = {
  fontSize: "32px",
  fontWeight: "bold",
  marginTop: "10px",
};

const btn = {
  marginRight: "12px",
  marginTop: "12px",
  padding: "10px 16px",
  border: "none",
  borderRadius: "6px",
  background: "#4f46e5",
  color: "#fff",
  cursor: "pointer",
};

export default Dashboard;
