import React from "react";
import jsPDF from "jspdf";

function Reports() {
  const generatePDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(18);
    doc.text("Employee Management System", 20, 20);

    doc.setFontSize(14);
    doc.text("System Report", 20, 35);

    doc.setFontSize(11);
    doc.text("• Total Employees: Auto-calculated", 20, 55);
    doc.text("• Attendance Status: System Active", 20, 65);
    doc.text("• Generated on: " + new Date().toLocaleString(), 20, 75);

    doc.save("EMS_Report.pdf");
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1>Reports</h1>

      <p style={{ color: "#555", marginBottom: "20px" }}>
        Generate official system reports for review and submission.
      </p>

      <button
        onClick={generatePDF}
        style={{
          padding: "12px 18px",
          backgroundColor: "#4f46e5",
          color: "#fff",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
          fontSize: "14px",
        }}
      >
        Download PDF Report
      </button>
    </div>
  );
}

export default Reports;
